import './App.css';
import Header from './componentes/header';
import styled from 'styled-components';

const AppContainer = styled.div`
    width: 100vw;     /* !!! esse '100vw' quer dizer todo o espaço HORIZONTAL da tela */
    height: 100vh;    /* !!! esse '100vh' quer dizer todo o espaço VERTICAL da tela */
    background-image: linear-gradient(90deg, #002F52 35%, #326589 165%);

  li {    /* quando não colocamos o ponto (.) antes quer dizer que o parametro servirá para todo estilo ao longo do projeto */
      list-style: none; 
  }

`

function App() {
  return (
    <div className='App'>
      <Header/>
    </div>
  );
}

export default App;
