import styled from 'styled-components';

const OpcoesHeaderr = styled.ul`
  display: flex;  
`

const Opcao = styled.li`
  font-size: 16;
  display: flex;
  justify-content: center;
  align-items: center;
  text-align: center;
  height: 100%;
  padding: 0 5px;
  cursor: pointer;
  min-width: 120px;    
`

const textoOpcoes = ['CATEGORIAS', 'FAVORITOS', 'MINHA ESTANTE', 'PERFIL'];

function OpcoesHeader(){
    return(
        <OpcoesHeaderr>
          { textoOpcoes.map(function (texto){
            return <Opcao className='opcao'><p>{texto}</p></Opcao>
          })}
        </OpcoesHeaderr>
    )
}

export default OpcoesHeader
